reportesPersonalController.getDataPersonalizada = async (req, res) => {
  const {
    NOMBRE,
    SEXO,
    EDAD,
    TIPONOM,
    NOMCATE,
    ADSCRIPCION,
    CLAVE,
    STATUS_EMPLEADO,
    PRINT,
    RESPONSABLE,
  } = req.body;

  let filtro = {};
  let nombreText = "";
  let sexoText = "";
  let edadText = "";
  let tipoNomText = "";
  let nomcateText = "";
  let adscripText = "";
  let claveText = "";
  let statusText = "";
  if (SEXO) {
    filtro.SEXO = SEXO;
    sexoText = `EL SEXO "${SEXO === "H" ? "HOMBRE" : "MUJER"}"`;
  }

  if (TIPONOM) {
    if (TIPONOM === "B") {
      filtro.TIPONOM = { $in: ["F51", "M51"] };
      tipoNomText = `EL TIPO DE NOMBRAMIENTO DE "BASE"`;
    } else if (TIPONOM === "CC") {
      filtro.TIPONOM = { $in: ["FCT", "CCT"] };
      tipoNomText = `EL TIPO DE NOMBRAMIENTO DE "CONTRATO CONFIANZA"`;
    } else if (TIPONOM === "NC") {
      filtro.TIPONOM = { $in: ["FCO", "511"] };
      tipoNomText = `EL TIPO DE NOMBRAMIENTO DE NOMBRAMIENTO CONFIANZA"`;
    } else if (TIPONOM === "MM") {
      filtro.TIPONOM = { $in: ["FMM", "MMS"] };
      tipoNomText = `EL TIPO DE NOMBRAMIENTO DE "MANDOS MEDIOS"`;
    }
  }
  if (NOMCATE) {
    console.log(NOMCATE.CLAVE_CATEGORIA);

    filtro.CLAVECAT = NOMCATE.CLAVE_CATEGORIA;
    nomcateText = `LA CATEGORÍA "${NOMCATE.DESCRIPCION}"`;
  }
  if (ADSCRIPCION) {
    filtro.ADSCRIPCION = ADSCRIPCION;
    adscripText = `LA ADSCRIPCIÓN "${ADSCRIPCION}"`;
  }
  if (CLAVE) {
    if (Array.isArray(CLAVE)) {
      filtro.CLAVE = { $in: CLAVE };
      claveText = `${
        CLAVE.length > 1 ? "LAS CLAVES" : "LA CLAVE"
      } "${CLAVE.join(", ")}"`;
    } else {
      filtro.CLAVE = CLAVE;
      claveText = `LA CLAVE "${CLAVE}"`;
    }
  }
  if (STATUS_EMPLEADO) {
    if (STATUS_EMPLEADO === "ACTIVO") {
      filtro["$or"] = [
        { STATUS_EMPLEADO: null },
        { "STATUS_EMPLEADO.STATUS": null },
      ];
      statusText = `EL STATUS DE "ACTIVO"`;
    } else {
      filtro["STATUS_EMPLEADO.STATUS"] = STATUS_EMPLEADO;
      if (STATUS_EMPLEADO === "COM_SDCL")
        statusText = `EL STATUS DE "COMISIONADO AL SINDICATO"`;
      else if (STATUS_EMPLEADO === "COM_LAB")
        statusText = `EL STATUS DE "COMISIONADO LABORALMENTE"`;
      else if (STATUS_EMPLEADO === "ASIG_LAB")
        statusText = `EL STATUS DE "ASIGNADO LABORALMENTE"`;
      else if (STATUS_EMPLEADO === "EXIMA") statusText = `EL STATUS DE "EXIMA"`;
    }
  }
  filtro.status = 1;

  const employees = await query("PLANTILLA", filtro);

  if (!employees || employees.length === 0) {
    return res
      .status(202)
      .json({ message: "No hay empleados con los filtros especificados" });
  }

  let empleadosFiltrados = employees;
  if (NOMBRE) {
    const nombreBuscado = NOMBRE.replace(/\s+/g, " ").trim().toLowerCase();
    empleadosFiltrados = employees.filter((emp) => {
      const nombreCompleto = `${emp.APE_PAT} ${emp.APE_MAT} ${emp.NOMBRES}`
        .replace(/\s+/g, " ")
        .trim()
        .toLowerCase();
      return nombreCompleto.includes(nombreBuscado);
    });
    nombreText = `EL NOMBRE "${NOMBRE}"`;
  }
  if (EDAD) {
    const match = /^(\d+)\s*[aA]\s*(\d+)$/.exec(EDAD);

    if (match) {
      const minEdad = parseInt(match[1], 10);
      const maxEdad = parseInt(match[2], 10);

      const hoy = new Date();

      function calcularEdad(fechaNac) {
        const fecha = new Date(fechaNac);
        let edad = hoy.getFullYear() - fecha.getFullYear();
        const m = hoy.getMonth() - fecha.getMonth();
        if (m < 0 || (m === 0 && hoy.getDate() < fecha.getDate())) {
          edad--;
        }
        return edad;
      }

      empleadosFiltrados = empleadosFiltrados.filter((emp) => {
        if (!emp.FECHA_NAC || emp.FECHA_NAC === "" || emp.FECHA_NAC === "null")
          return false;

        const edad = calcularEdad(emp.FECHA_NAC);
        return edad >= minEdad && edad <= maxEdad;
      });
    }
    edadText = `LA EDAD ENTRE "${
      match ? `${match[1]} A ${match[2]} AÑOS"` : ""
    }`;
  }

  empleadosFiltrados = empleadosFiltrados
    .map((emp) => ({
      _id: emp._id,
      NOMBRE: `${emp.APE_PAT} ${emp.APE_MAT} ${emp.NOMBRES}`,
      SEXO: emp.SEXO,
      TIPONOM: emp.TIPONOM,
      NUMPLA: emp.NUMPLA,
      ADSCRIPCION: emp.ADSCRIPCION,
      NOMCATE: emp.NOMCATE,
      FECHA_NAC: emp.FECHA_NAC,
      STATUS_EMPLEADO: emp?.STATUS_EMPLEADO?.STATUS || "",
    }))
    .sort((a, b) => a.NOMBRE.localeCompare(b.NOMBRE));

  if (empleadosFiltrados.length === 0) {
    return res
      .status(202)
      .json({ message: "No hay empleados con el nombre especificado" });
  }

  if (!PRINT) {
    res.status(202).json({ empleadosFiltrados });
  } else {
    const PDFDocument = require("pdfkit");
    const doc = new PDFDocument({ layout: "landscape", margin: 40 });

    const tipoNomMapping = {
      M51: "BASE FORÁNEA",
      F51: "BASE CENTRAL",
      FCT: "CONTRATO CONFIANZA FORANEO",
      CCT: "CONTRATO CONFIANZA CENTRAL",
      FCO: "NOMBRAMIENTO CONFIANZA FORANEO",
      511: "NOMBRAMIENTO CONFIANZA CENTRAL",
      F53: "CONTRATO FORÁNEO",
      M53: "CONTRATO CENTRAL",
      MMS: "MANDOS MEDIOS FORÁNEOS",
      FMM: "MANDOS MEDIOS CENTRAL",
    };

    const tipoStatusMapping = {
      COM_SDCL: "COMISIONADO AL SINCICATO",
      COM_LAB: "COMISIONADO LABORALMENTE",
      ASIG_LAB: "ASIGNADO LABORALMENTE",
      EXIMA: "EXIMA",
    };

    const rows = empleadosFiltrados.map((emp, i) => {
      let row = [
        `${i + 1}`,
        emp.NOMBRE,
        emp.SEXO === "H" ? "HOMBRE" : "MUJER",
        tipoNomMapping[emp.TIPONOM] ?? emp.TIPONOM,
        emp.NUMPLA || "",
        emp.ADSCRIPCION || "",
        emp.NOMCATE || "",
        tipoStatusMapping[emp?.STATUS_EMPLEADO] ?? "ACTIVO",
      ];
      return row;
    });

    console.log("Inicio de la función"); // Verifica si el flujo llega aquí
    if (rows.length > 0) {
      console.log("Condición cumplida");
    }

    const filePath = path.join(
      __dirname,
      `../../docs/reportes/personal/LISTA_PERSONAL.pdf`
    );

    try {
      const stream = fs.createWriteStream(filePath);
      const currentDate = new Date().toLocaleDateString("es-MX");

      stream.on("error", (err) => {
        console.error("Error al escribir el archivo:", err.message);
        res.status(500).json({ message: "Error al generar el reporte." });
        doc.end();
      });

      stream.on("finish", () => {
        res.setHeader("Content-Type", "application/pdf");
        res.setHeader(
          "Content-Disposition",
          `attachment; filename=LISTA_PERSONAL.pdf`
        );
        res.download(filePath, `LISTA_PERSONAL.pdf`, (err) => {
          if (err) {
            console.error("Error al descargar el archivo:", err.message);
            res.status(500).json({ message: "Error al descargar el archivo." });
          }
        });
      });

      doc.pipe(stream);

      doc.registerFont("Consolas", fontPath);
      doc.font("Consolas").fontSize(9);

      const base64 =
        "data:image/png;base64,";
      const data = base64.split(",")[1];

      let pageNumber = 0;
      const addHeaderAndFooter = () => {
        doc.image(Buffer.from(data, "base64"), {
          width: 120,
        });
        pageNumber++;
        // Footer
        const footerY = doc.page.height - doc.page.margins.bottom - 20;
        doc
          .fontSize(10)
          .text(`PÁGINA ${pageNumber}`, 60, footerY, { align: "right" });
        // Header
        doc
          .fontSize(10)
          .text(`FECHA: ${currentDate}`, 60, 20, { align: "right" });
        doc
          .fontSize(12)
          .text("GOBIERNO DEL ESTADO DE OAXACA", { align: "center" });
        doc.fontSize(12).text("SECRETARÍA DE FINANZAS", { align: "center" });
        doc
          .fontSize(12)
          .text("DEPARTAMENTO DE RECURSOS HUMANOS", { align: "center" });
        doc.fontSize(12).text("LISTA DE PERSONAL", { align: "center" });
        doc.fontSize(10);
      };

      doc.on("pageAdded", addHeaderAndFooter);
      doc.margins = { top: 72, bottom: 72, left: 60, right: 60 };
      addHeaderAndFooter();

      let y = doc.y + 10;
      // ...resto del código...
      // --- TABLA MANUAL ---
      const headers = [
        "#",
        "NOMBRE",
        "SEXO",
        "NOMBRAMIENTO",
        "PLAZA",
        "ADSCRIPCIÓN",
        "PUESTO",
        "STATUS",
      ];
      const columns = [30, 150, 40, 80, 40, 170, 100, 100];
      const rowHeight = 18;

      let x = doc.page.margins.left;
      headers.forEach((header, i) => {
        // Dibuja el fondo
        doc
          .rect(x, y, columns[i], rowHeight)
          .fillAndStroke("#6C6E6D", "#000000");
        // Cambia el color de texto a blanco
        doc
          .fillColor("#FFFFFF")
          .text(header, x + 2, y + 4, { width: columns[i] - 4, align: "left" });
        // Regresa el color de texto a negro para el resto del documento
        doc.fillColor("#000000");
        x += columns[i];
      });
      y += rowHeight;

      // Dibuja filas
      for (let i = 0; i < rows.length; i++) {
        x = doc.page.margins.left;

        // Calcular el alto máximo de la fila
        const cellHeights = rows[i].map(
          (cell, j) =>
            doc.heightOfString(String(cell), {
              width: columns[j] - 4,
              align: "left",
            }) + 4
        );
        const maxRowHeight = Math.max(...cellHeights, 18);
        const FOOTER_HEIGHT = 20;

        // Salto de página si no cabe la fila
        if (
          y + maxRowHeight >
          doc.page.height - doc.page.margins.bottom - FOOTER_HEIGHT
        ) {
          doc.addPage();
          y = doc.y + 10;
          // Redibuja encabezados
          let xh = doc.page.margins.left;
          headers.forEach((header, j) => {
            doc.rect(xh, y, columns[j], 18).fillAndStroke("#6C6E6D", "#000000");
            doc.fillColor("#FFFFFF").text(header, xh + 2, y + 4, {
              width: columns[j] - 4,
              align: "left",
            });
            doc.fillColor("#000000");
            xh += columns[j];
          });
          y += 18;
        }

        // Dibuja la fila
        rows[i].forEach((cell, j) => {
          // Si la fila es par, pinta el fondo
          if (i % 2 === 1) {
            doc
              .rect(x, y, columns[j], maxRowHeight)
              .fillAndStroke("#fff", "#000000");
          } else {
            doc.rect(x, y, columns[j], maxRowHeight).stroke();
          }
          doc.fillColor("#000000").text(String(cell), x + 2, y + 4, {
            width: columns[j] - 4,
            align: "left",
          });
          x += columns[j];
        });
        y += maxRowHeight;
      }
      doc.moveDown(3);

      const centerY = doc.page.height / 2;
      const filtros = [];
      if (nombreText) filtros.push(nombreText);
      if (edadText) filtros.push(edadText);
      if (sexoText) filtros.push(sexoText);
      if (tipoNomText) filtros.push(tipoNomText);
      if (nomcateText) filtros.push(nomcateText);
      if (adscripText) filtros.push(adscripText);
      if (claveText) filtros.push(claveText);
      if (statusText) filtros.push(statusText);

      let filtrosStr = "";
      if (filtros.length === 1) {
        filtrosStr = filtros[0];
      } else if (filtros.length === 2) {
        filtrosStr = `${filtros[0]} Y ${filtros[1]}`;
      } else if (filtros.length > 2) {
        filtrosStr = `${filtros.slice(0, -1).join(", ")} Y ${
          filtros[filtros.length - 1]
        }`;
      }

      const filtroTexto =
        filtros.length === 1
          ? `CONFORME AL FILTRO DE ${filtrosStr} SE ENCONTRARON EL TOTAL DE: ${
              empleadosFiltrados.length
            } ${
              empleadosFiltrados.length > 1
                ? "EMPLEADOS QUE CUMPLEN"
                : "EMPLEADO QUE CUMPLE"
            } CON EL CRITERIO.`
          : filtros.length > 1
          ? `CONFORME A LOS SIGUIENTES FILTROS DE BÚSQUEDA: ${filtrosStr} SE ENCONTRARON EL TOTAL DE: ${
              empleadosFiltrados.length
            } ${
              empleadosFiltrados.length > 1
                ? "EMPLEADOS QUE CUMPLEN"
                : "EMPLEADO QUE CUMPLE"
            } CON LOS CRITERIOS.`
          : "";

      doc.text(filtroTexto, doc.page.margins.left, y + 10, {
        width: doc.page.width - doc.page.margins.left - doc.page.margins.right,
        align: "justify",
      });
      doc.moveDown();
      doc.text(
        `ESTE REPORTE FUE DESCARGADO MEDIANTE EL SISTEMA INTEGRAL DE RECURSOS HUMANOS (SIRH), Y FUE GENEARADO POR EL USUARIO: ${RESPONSABLE}`,
        doc.page.margins.left,
        doc.y,
        {
          width:
            doc.page.width - doc.page.margins.left - doc.page.margins.right,
          align: "left",
        }
      );

      doc.end();
    } catch (error) {
      console.error("Error al crear el archivo:", error.message);
      res.status(500).json({ message: "Error al generar el reporte." });
    }
  }
};
reportesPersonalController.getPlantillaXLSX = async (req, res) => {
  try {
    // Obtener el parámetro status de los params de la ruta y convertirlo a entero
    const statusParam = req.params.status ? parseInt(req.params.status, 10) : 1;

    const filtro = { status: statusParam };
    const excludeFields = {
      ID_CTRL_ASSIST: 0,
      ID_CTRL_TALON: 0,
      ID_CTRL_NOM: 0,
      ID_CTRL_CAP: 0,
      VACACIONES: 0,
      status: 0,
      AREA_RESP: 0,
      ID_BITACORA: 0,
      STATUS_EMPLEADO: 0,
      ID_CTRL_ASIST: 0,
      _id: 0,
    };
    const plantilla = await query("PLANTILLA", filtro, excludeFields);

    if (!plantilla || plantilla.length === 0) {
      return res
        .status(404)
        .json({ message: "No hay registros de plantilla." });
    }

    // Crea el libro y la hoja
    const ExcelJS = require("exceljs");
    const moment = require("moment");
    const workbook = new ExcelJS.Workbook();
    const worksheet = workbook.addWorksheet("PLANTILLA");

    // Determina las columnas a exportar
    const campos = Object.keys(plantilla[0]);

    // Agrega solo los encabezados de las columnas
    worksheet.addRow(campos.map((c) => c.toUpperCase()));

    // Agrega los datos
    plantilla.forEach((item) => {
      worksheet.addRow(campos.map((c) => item[c]));
    });

    // Ajusta el ancho de columnas
    worksheet.columns.forEach((col) => {
      col.width = 20;
    });

    // Genera el archivo y responde para descarga
    const fechaStr = moment().format("YYYY-MM-DD_HH-mm-ss");
    const tipoRegistro =
      statusParam === 1
        ? "ACTIVOS"
        : statusParam === 2
        ? "VACANTES"
        : "PLANTILLA";
    const fileName = `PLANTILLA_${tipoRegistro}_${fechaStr}.xlsx`;
    res.setHeader(
      "Content-Type",
      "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
    );
    res.setHeader("Content-Disposition", `attachment; filename=${fileName}`);

    await workbook.xlsx.write(res);
    res.end();
  } catch (error) {
    console.error("Error al generar el archivo Excel:", error.message);
    res.status(500).json({ message: "Error al generar el archivo Excel." });
  }
};
module.exports = reportesPersonalController;
